library(tidyverse)
library(tidytuesdayR)
library(tinytable)
library(mgcv)
library(marginaleffects)
library(ggdist)
library(ggdensity)
library(geomtextpath)
library(ggrepel)
library(plotly)

library(patchwork)
set.seed(2025-07-08)
```
