## General modifier analysis


::: {.cell}

```{.r .cell-code}
make_mod_comp <- function(color_hls, mod = "light"){
  color_hls |> 
    filter(
      n_color == 1,
      str_detect(color, " ", negate = T)
    ) |> 
    mutate(
      base = color
    ) |> 
    select(
      base, H:S, hex
    ) ->
    base_cols

  color_hls |> 
    filter(
      n_color == 1,
      str_detect(color, str_c("^", mod, " "))
    ) |> 
    mutate(
      base = str_remove(color, mod) |> str_squish()
    ) |> 
    select(
      base, H:S, hex
    ) ->
    mod_cols

  mod_cols |> 
    left_join(
      base_cols,
      by = "base",
      suffix = c("_mod", "_base")
    ) |>  
    mutate(
      H_diff = H_mod - H_base,
      L_diff = L_mod - L_base,
      S_diff = S_mod - S_base,
      H_diff = case_when(
        H_diff > 180 ~ H_diff - 360,
        H_diff < -180 ~ H_diff + 360,
        .default = H_diff
      ),
      modfier = mod
    ) |> 
    filter(
      if_all(
        ends_with("_diff"),
        is.finite
      )
    ) 
}
