make_mod_dotplot <- function(mod_comp){
  mod_comp |> 
    pivot_longer(
      ends_with("_diff")
    ) |> 
    mutate(
      name = case_match(
        name,
        "H_diff" ~ "Hue",
        "L_diff" ~ "Lightness",
        "S_diff" ~ "Saturation"
      )
    ) |> 
    ggplot(
      aes(value)
    ) +
    geom_vline(
      xintercept = 0,
      color = "grey60"
    ) +
    geom_dots(
      aes(
        fill = hex_base,
        order = L_base
      ),
      group = 1
    ) +
    scale_fill_identity() +
    labs(x = '"light" shift')+
    facet_wrap(~name, scales = "free_x") +
    theme_no_y() 
}
