make_mod_shiftplot <- function(mod_comp){
  mod_comp |> 
    pivot_longer(
      matches("[A-Z]_", ignore.case = F)
    ) |>
    rename(
      base_col = base
    ) |> 
    separate_wider_delim(
      name,
      delim = "_",
      names = c("var", "comp")
    ) |> 
    pivot_wider(
      names_from = comp,
      values_from = value
    ) |> 
    ggplot(
      aes(
        base, diff
      )
    ) +
    geom_hline(yintercept = 0) +
    geom_point(
      aes(fill = hex_base),
      shape = 21,
      size = 3
    ) +
    stat_smooth(
      method = gam,
      formula = y ~ s(x, bs = "cs"),
      se = F
    ) +
    scale_fill_identity()+
    facet_wrap(~var, scales = "free")
}
