make_mod_lsvector <- function(mod_comp){
  mod <- mod_comp$modifier[1]
  mod_comp |> 
    ggplot(
      aes(
        L_base, S_base
      )
    )+
    geom_segment(
      aes(
        xend = L_mod,
        yend = S_mod,
        color = hex_mod
      ),
      arrow = arrow(
        type = "closed",
        length = unit(0.2, "cm")
      )
    ) +
    geom_point(
      aes(
        fill = hex_base
      ),
      shape = 21, 
      size = 3
    ) +
    scale_color_identity(aesthetics = c("colour", "fill")) +
    scale_x_continuous(
      "Lightness",
      limits = c(0,1)
    ) +
    scale_y_continuous(
      "Saturation",
      limit = c(0, 1)
    )+
    coord_fixed() +
    labs(title = str_glue("{mod} <color>"))
}
