```{.r .cell-code}
color_hls |> 
  make_mod_comp("light") |> 
  mutate(
    across(
      starts_with("H_"),
        ~.x * (pi/180)
      ),
      x_base = sin(H_base) * sqrt(
        abs((L_base^2)-(S_base^2))
      ),
      y_base = cos(H_base) * sqrt(
        abs((L_base^2)-(S_base^2))
      ),
      x_mod = sin(H_base) * sqrt(
        abs((L_mod^2)-(S_mod^2))
      ),
      y_mod = cos(H_base) * sqrt(
        abs((L_mod^2)-(S_mod^2))
      ),
    hemi_base = case_when(
      L_base <= 0.5 ~ "dark",
      .default = "light"
    ),
    hemi_mod = case_when(
      L_mod <= 0.5 ~ "dark",
      .default = "light"
    )
  ) ->
  sphere_vec

sphere_vec |> 
  pivot_longer(
      c(x_base, y_base,L_base,
        x_mod, y_mod,L_mod
      )
    ) |> 
  separate_wider_delim(
    name, 
    delim = "_",
    names = c("dim", "shade")
  ) |> 
  pivot_wider(
    names_from = dim,
    values_from = value
  ) |> 
  nest(
    .by = c(base, hex_mod),
    data =c(hex_mod, x:L)
  ) ->
  segments 

plot_ly() |> 
  add_trace(
    type = 'scatter3d', 
    mode = "markers",
    x = sphere_vec$x_base,
    y = sphere_vec$y_base,
    z = sphere_vec$L_base*2,
    marker = list(
      color = sphere_vec$hex_base
    )
  )  ->
  p


for(i in seq_along(segments$data)){
  d <- segments$data[[i]]
  p |> 
    add_trace(
      type = 'scatter3d',
      mode = "lines",
      x = d$x,
      y = d$y,
      z = d$L*2,
      line = list(color = d$hex_mod, width = 3)
    )->
    p
}

p |> layout(showlegend = F)
```

