expand_grid(
  x = seq(-1,1, length = 300),
  y = seq(-1,1, length = 300)
) |> 

  mutate(
    L = sqrt((x^2) + (y^2))
  ) |> 
  filter(
    L <= 0.9
  ) |> 
  mutate(
    H_rad = atan2(x,y),
    H_rad = case_when(
      H_rad < 0 ~ H_rad + (2*pi),
      .default = H_rad
    ),
    H = H_rad * (180/pi)
  ) |> 
  rowwise() |> 
  mutate(
    hls = HLS(H, L, 0.75) |>  list(),
    hex = hex(hls)
  ) ->
  color_wheel
